class s{static getCSRF(){const t=document.querySelector('meta[name="csrf-token"]');return t?t.getAttribute("content"):null}static sendPost(t,e){return fetch(t,{method:"POST",headers:{"X-CSRF-TOKEN":s.getCSRF(),"Content-Type":"application/json;charset=utf-8"},credentials:"include",body:e}).then(n=>n.json())}static sendGet(t,e={}){return fetch(t,{method:"GET",headers:{"X-CSRF-TOKEN":s.getCSRF(),"Content-Type":"application/json;charset=utf-8",...e},credentials:"include"}).then(n=>n.json())}static sendPatch(t,e,n={}){return fetch(t,{method:"PATCH",headers:{"X-CSRF-TOKEN":s.getCSRF(),"Content-Type":"application/json;charset=utf-8",...n},credentials:"include",body:e}).then(c=>c.json())}}export{s as F};
